// src/api.js
import axios from "axios";

// Configura la URL de tu backend Django
const api = axios.create({
  baseURL: "http://localhost:8000", // 游녣 cambia si tu backend corre en otro puerto
});

// Interceptor para manejar errores (ej: token inv치lido)
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response && error.response.status === 401) {
      // Aqu칤 detectamos cuando el token est치 malo
      alert("丘멆잺 Token inv치lido o expirado");
    }
    return Promise.reject(error);
  }
);

export default api;
